'use strict';

const node_path = require('node:path');
const citty = require('citty');
const index = require('./shared/listhen.e3086696.cjs');
require('node:http');
require('node:https');
require('node:util');
require('get-port-please');
require('http-shutdown');
require('defu');
require('consola/utils');
require('node:child_process');
require('node:fs');
require('node:os');
require('mlly');
require('consola');

const name = "listhen";
const version = "1.1.1";
const description = "ğŸ‘‚ Elegant HTTP Listener";

const main = citty.defineCommand({
  meta: {
    name,
    description,
    version
  },
  args: {
    cwd: {
      type: "string",
      description: "Current working directory"
    },
    entry: {
      type: "positional",
      description: "Listener entry file (./app.ts)",
      required: true
    },
    port: {
      type: "string",
      description: "Port to listen on (use PORT environment variable to override)"
    },
    host: {
      type: "string",
      description: "Host to listen on (use HOST environment variable to override)"
    },
    clipboard: {
      type: "boolean",
      description: "Copy the URL to the clipboard",
      default: false
    },
    open: {
      type: "boolean",
      description: "Open the URL in the browser",
      default: false
    },
    baseURL: {
      type: "string",
      description: "Base URL to use"
    },
    name: {
      type: "string",
      description: "Name to use in the banner"
    },
    https: {
      type: "boolean",
      description: "Enable HTTPS",
      default: false
    },
    watch: {
      type: "boolean",
      description: "Watch for changes",
      alias: "w",
      default: false
    }
  },
  async run({ args }) {
    const cwd = node_path.resolve(args.cwd || ".");
    process.chdir(cwd);
    const opts = {
      ...args,
      cwd,
      port: args.port,
      hostname: args.host,
      clipboard: args.clipboard,
      open: args.open,
      baseURL: args.baseURL,
      name: args.name,
      https: args.https
      // TODO: Support custom cert
    };
    if (args.watch) {
      await index.listenAndWatch(args.entry, opts);
    } else {
      const importer = await index.createImporter(args.entry);
      const handler = await importer.import();
      await index.listen(handler, opts);
    }
  }
});
const runMain = () => citty.runMain(main);

exports.main = main;
exports.runMain = runMain;
